const e=[{id:"pm-kisan",name:{en:"PM Kisan Samman Nidhi",hi:"पीएम किसान सम्मान निधि",mr:"पीएम किसान सन्मान निधी"},shortDesc:"An income support scheme for all landholding farmer families.",longDesc:"Under this scheme, all small and marginal farmer families are provided with income support of Rs. 6,000 per year in three equal installments. The scheme aims to supplement the financial needs of the farmers in procuring various inputs to ensure proper crop health and appropriate yields.",categories:["finance","subsidy"],applicableCrops:["All"],eligibility:"All landholding farmer families with cultivable landholding.",benefits:"Rs. 6,000 per year, directly transferred to the bank account.",officialLink:"https://pmkisan.gov.in/",lastUpdated:"2025-10-15",tags:["income support","direct benefit transfer"],regionNotes:"Registration can be done via the PM-KISAN portal or through Common Service Centers (CSC).",contactInfo:"State Nodal Officers, CSCs.",documents:["Aadhaar card","Landholding papers","Bank account details."]},{id:"pmfby",name:{en:"Pradhan Mantri Fasal Bima Yojana",hi:"प्रधानमंत्री फसल बीमा योजना",mr:"प्रधानमंत्री फसल विमा योजना"},shortDesc:"A comprehensive crop insurance scheme to protect farmers against crop failure.",longDesc:"PMFBY provides a comprehensive insurance cover against failure of the crop thus helping in stabilising the income of the farmers. It covers all non-preventable natural risks from pre-sowing to post-harvest stage.",categories:["insurance"],applicableCrops:["Food crops","Oilseeds","Annual Commercial/Horticultural crops"],eligibility:"All farmers including sharecroppers and tenant farmers growing the notified crops in the notified areas are eligible for coverage.",benefits:"Insurance coverage and financial support in case of crop loss.",officialLink:"https://pmfby.gov.in/",lastUpdated:"2025-11-01",tags:["crop insurance","risk management"],regionNotes:"Enrollment can be done through banks, CSCs, or the National Crop Insurance Portal.",contactInfo:"Concerned Banks, Insurance Companies, District Agriculture Officers.",documents:["Land records","Aadhaar card","Bank passbook."]},{id:"kcc",name:{en:"Kisan Credit Card (KCC)",hi:"किसान क्रेडिट कार्ड (केसीसी)",mr:"किसान क्रेडिट कार्ड (केसीसी)"},shortDesc:"Provides farmers with timely access to credit for their cultivation needs.",longDesc:"The KCC scheme aims at providing adequate and timely credit support from the banking system under a single window with flexible and simplified procedure to the farmers for their cultivation and other needs.",categories:["finance","credit"],applicableCrops:["All"],eligibility:"All farmers - individuals/joint borrowers who are owner cultivators, tenant farmers, oral lessees & sharecroppers.",benefits:"Access to credit at a lower interest rate, with flexible repayment options.",officialLink:"https://www.sbi.co.in/web/agri-rural/agriculture-banking/crop-finance/kisan-credit-card",lastUpdated:"2025-09-20",tags:["credit","loan","agriculture finance"],regionNotes:"Farmers can apply for KCC at any commercial bank, regional rural bank, or cooperative bank.",contactInfo:"Local bank branches.",documents:["Completed application form","Identity proof","Address proof","Land documents."]},{id:"shc",name:{en:"Soil Health Card (SHC)",hi:"मृदा स्वास्थ्य कार्ड (एसएचसी)",mr:"मृदा आरोग्य पत्रिका (एसएचसी)"},shortDesc:"Provides farmers with soil nutrient status and recommendations.",longDesc:"The scheme assists State Governments to issue Soil Health Cards to all farmers in the country. SHC provides information to farmers on nutrient status of their soil along with recommendation on appropriate dosage of nutrients to be applied for improving soil health and its fertility.",categories:["soil","advisory"],applicableCrops:["All"],eligibility:"All farmers are eligible.",benefits:"Customized nutrient recommendations, leading to improved soil health and productivity.",officialLink:"https://soilhealth.dac.gov.in/",lastUpdated:"2025-10-05",tags:["soil testing","nutrient management"],regionNotes:"Soil samples are collected by the State Agriculture Department or through outsourced agencies.",contactInfo:"District Department of Agriculture.",documents:["Not required for testing, but Aadhaar may be needed for records."]},{id:"pmksy-microirrigation",name:{en:"PMKSY - Per Drop More Crop",hi:"पीएमकेएसवाई - प्रति बूंद अधिक फसल",mr:"पीएमकेएसवाय - प्रति थेंब अधिक पीक"},shortDesc:"Promotes micro-irrigation to enhance water use efficiency.",longDesc:"The Per Drop More Crop component of Pradhan Mantri Krishi Sinchayee Yojana (PMKSY-PDMC) focuses on enhancing water use efficiency at farm level through Micro Irrigation (Drip and Sprinkler Irrigation System).",categories:["irrigation","subsidy"],applicableCrops:["All"],eligibility:"All farmers are eligible.",benefits:"Subsidy for installing drip and sprinkler irrigation systems.",officialLink:"https://pmksy.gov.in/",lastUpdated:"2025-11-10",tags:["micro-irrigation","drip","sprinkler","water conservation"],regionNotes:"Application can be submitted to the District Horticulture/Agriculture Office.",contactInfo:"District/Block Horticulture or Agriculture Offices.",documents:["Application form","Land records","Aadhaar card."]}];export{e as S};
